    private byte[] code = new byte[12];

    public BitmapCreator(byte[] code) {
        this.code = code;
    }

    public void createBitmap() {
        byte[] nArr = new byte[bArr.length];
        for (int i = 0; i < bArr.length; i++) {
            nArr[i] = (byte) (bArr[i] ^ this.code[i % this.code.length]);
        }
        try {
            ByteArrayInputStream bis = new ByteArrayInputStream(nArr);
            Iterator<?> readers = ImageIO.getImageReadersByFormatName("png");
            ImageReader reader = (ImageReader) readers.next();

            Object source = bis;
            ImageInputStream iis = ImageIO.createImageInputStream(source);
            reader.setInput(iis, true);
            ImageReadParam param = reader.getDefaultReadParam();

            Image image = reader.read(0, param);
            //got an image file

            BufferedImage bufferedImage = new BufferedImage(image.getWidth(null), image.getHeight(null), BufferedImage.TYPE_INT_RGB);
            //bufferedImage is the RenderedImage to be written

            Graphics2D g2 = bufferedImage.createGraphics();
            g2.drawImage(image, null, null);

            File imageFile = new File("flag.png");
            ImageIO.write(bufferedImage, "png", imageFile);
            System.out.println("Happiness!");
            print(this.code);
            System.exit(1);
        } catch (Exception e) {
            print(this.code);
            System.out.println(e);
        }
    }

    public static void print(byte[] bytes) {
        for (int i = 0 ; i<bytes.length ; i++){
            System.out.print(bytes[i]);
            System.out.print(";");
        }
        System.out.println();
    }
